#!/usr/bin/sh
#
# This is executed by systemd as defined in turtl-serverd.service
#
# This will send an email "from" EMAIL_FROM, "to" EMAIL_TO, and will have
# TURTLNODE_ALIAS as a subject line differentiator.
#
# Do not edit this file. These values are instead configured in
# /etc/sysconfig/turtl-serverd
#
# To enable email...
# 1. ensure mail can be sent from your system
# 2. set a value for EMAIL_TO, EMAIL_FROM, and TURTLNODE_ALIAS in the
#    configuration file above
#

EMAIL_FROM=$2
EMAIL_TO=$3
TURTLNODE_ALIAS=$4

DATE_LOG=$(/usr/bin/date --utc +"%b %d %T UTC")


start() {
  if [[ ! -z $EMAIL_FROM && ! -z $EMAIL_TO && ! -z $TURTLNODE_ALIAS ]] ; then
    /usr/bin/echo "[$TURTLNODE_ALIAS] START notification: Sending systemd-managed email- $DATE_LOG"
    /usr/bin/echo "[$TURTLNODE_ALIAS] START notification: systemd-managed daemon start" | /usr/bin/mail -S sendwait -s "[$TURTLNODE_ALIAS] turtl-serverd.service started - $DATE_LOG" -r $EMAIL_FROM $EMAIL_TO >> /dev/null 2>&1
  fi
}


stop() {
  if [[ ! -z $EMAIL_FROM && ! -z $EMAIL_TO && ! -z $TURTLNODE_ALIAS ]] ; then
    /usr/bin/echo "[$TURTLNODE_ALIAS] STOP notification: Sending systemd-managed email- $DATE_LOG"
    /usr/bin/echo "[$TURTLNODE_ALIAS] STOP notification: systemd-managed daemon stop" | /usr/bin/mail -S sendwait -s "[$TURTLNODE_ALIAS] turtl-serverd.service stopped - $DATE_LOG" -r $EMAIL_FROM $EMAIL_TO >> /dev/null 2>&1
  fi
}


case "$1" in
  start)
    start
    ;;
  stop)
    stop
    ;;
  *) exit 1
esac

